import axios from 'axios'
import { toastr } from 'react-redux-toastr'
import { initialize } from 'redux-form'
import { showTabs, selectTab } from '../common/tab/tabActions'
import consts from '../consts'

const INITIAL_VALUES = {}

export function getList() {
    const request = axios.get(`${consts.API_URL}/rooms`)
    return {
        type: 'ROOMS_FETCHED',
        payload: request
    }
}

export const create = (room) => {
    return dispatch => {
        axios.post('/api/rooms', room)
            .then(resp => {
                dispatch({ type: 'ROOM_CREATE', payload: resp.data })
            })
            .catch(error => {
                console.error("Ошибка при создании комнаты: ", error)
            })
    }
}
export const update = (room) => {
    return dispatch => {
        axios.put(`/api/rooms/${room.id}`, room)
            .then(resp => {
                dispatch({ type: 'ROOM_UPDATE', payload: resp.data })
            })
            .catch(error => {
                console.error("Ошибка при обновлении комнаты: ", error)
            })
    }
}

export const remove = (roomId) => {
    return dispatch => {
        axios.delete(`/api/rooms/${roomId}`)
            .then(resp => {
                dispatch({ type: 'ROOM_REMOVE', payload: roomId })
            })
            .catch(error => {
                console.error("Ошибка при удалении комнаты: ", error)
            })
    }
}

function submit(values, method) {
    return dispatch => {
        const id = values.id ? values.id : ''
        axios[method](`${consts.API_URL}/rooms/${id}`, values)
            .then(resp => {
                toastr.success('Успешно', 'Операция выполнена.')
                dispatch(init())
            })
            .catch(e => {
                e.response.data.errors.forEach(error => toastr.error('Ошибка', error))
            })
    }
}

export function prepareToShow(roomId, callback) {
    return dispatch => {
        axios['get'](`${consts.API_URL}/rooms/${roomId}`)
            .then(room => { dispatch(callback(room.data)) })
            .catch(e => {
                e.response.data.errors.forEach(error => toastr.error('Ошибка', error))
            })
    }
}

export function showUpdate(room) {
    return [
        showTabs('tabUpdate'),
        selectTab('tabUpdate'),
        initialize('roomForm', room)
    ]
}

export function showDelete(room) {
    return [
        showTabs('tabDelete'),
        selectTab('tabDelete'),
        initialize('roomForm', room)
    ]
}

export const init = () => {
    return dispatch => {
        // Инициализация действий, например, загрузка списка комнат
        axios.get('/api/rooms')
            .then(resp => {
                dispatch({ type: 'ROOM_LIST', payload: resp.data })
            })
            .catch(error => {
                console.error("Ошибка при загрузке комнат: ", error)
            })
    }
}
