import React, { Component } from 'react';
import { bindActionCreators } from 'redux';
import { connect } from 'react-redux';
import { getList, showUpdate, showDelete } from './employeeActions';

class EmployeeList extends Component {
    componentDidMount() {
        this.props.getList();
    }

    getFormatedDate(isoDate) {
        const options = { year: 'numeric', month: 'long', day: 'numeric' };
        const date = new Date(isoDate);
        return `${date.toLocaleDateString('ru-RU', options)} в ${date.toLocaleTimeString('ru-RU')}`;
    }

  renderRows() {
        const list = this.props.list || [];

        if (list.length === 0) {
            return (
                <tr>
                    <td colSpan="4">Сотрудников нет</td>
                </tr>
            );
        }

        return list.map((employee) => (
            <tr key={employee.id}>
                <td>{employee.name}</td>
                <td>{employee.identifier}</td>
                <td>{employee.created_at || '-'}</td>
                <td>
                    <button onClick={() => this.props.showUpdate(employee)}>Редактировать</button>
                    <button onClick={() => this.props.showDelete(employee)}>Удалить</button>
                </td>
            </tr>
        ));
    }

    render() {
        return (
            <table>
                <thead>
                    <tr>
                        <th>Имя</th>
                        <th>Идентификатор</th>
                        <th>Дата создания</th>
                        <th>Действия</th>
                    </tr>
                </thead>
                <tbody>{this.renderRows()}</tbody>
            </table>
        );
    }
}


const mapDispatchToProps = dispatch =>
    bindActionCreators({ getList, showUpdate, showDelete }, dispatch);

export default connect(mapStateToProps, mapDispatchToProps)(EmployeeList);
