import axios from 'axios'
import { toastr } from 'react-redux-toastr'
import { initialize } from 'redux-form'
import { showTabs, selectTab } from '../common/tab/tabActions'
import consts from '../consts'

const INITIAL_VALUES = {}

export function getList() {
    const request = axios.get(`${consts.API_URL}/equipments`)
    return {
        type: 'EQUIPMENTS_FETCHED',
        payload: request
    }
}

export function create(values) {
    return submit(values, 'post')
}

export function update(values) {
    return submit(values, 'put')
}

export function remove(equipment) {
    return dispatch => {
        const id = equipment.id // Получаем ID оборудования, которое нужно удалить
        axios.delete(`${consts.API_URL}/equipments/${id}`)
            .then(resp => {
                toastr.success('Success', 'Equipment deleted successfully.')
                dispatch({
                    type: 'EQUIPMENTS_DELETED',
                    payload: id // Удаляем оборудование из списка в Redux
                })
            })
            .catch(e => {
                toastr.error('Error', 'Failed to delete equipment.')
                e.response.data.errors.forEach(error => toastr.error('Error', error))
            })
    }
}

function submit(values, method) {
    return dispatch => {
        const id = values.id ? values.id : ''
        axios[method](`${consts.API_URL}/equipments/${id}`, values)
            .then(resp => {
                toastr.success('Success', 'Successful operation.')
                dispatch(init())
            })
            .catch(e => {
                e.response.data.errors.forEach(error => toastr.error('Error', error))
            })
    }
}

export function prepareToShow(equipmentId, callback) {
    return dispatch => {
        axios['get'](`${consts.API_URL}/equipments/${equipmentId}`)
            .then(equipment => { dispatch(callback(equipment.data)) })
            .catch(e => {
                e.response.data.errors.forEach(error => toastr.error('Error', error))
            })
    } // Закрытие функции prepareToShow
}

export function showUpdate(equipment) {       
    return [
        showTabs('tabUpdate'),
        selectTab('tabUpdate'),
        initialize('equipmentForm', equipment)
    ]
}

export function showDelete(equipment) {
    return [
        showTabs('tabDelete'),
        selectTab('tabDelete'),
        initialize('equipmentForm', equipment)
    ]
}

export function init() {
    return [
        showTabs('tabList', 'tabCreate'),
        selectTab('tabList'),
        getList(),
        initialize('equipmentForm', INITIAL_VALUES)
    ]
}
