import React from 'react';

class FileUploadComponent extends React.Component {
    state = {
      uploading: false,
      error: null
    };
  
    handleFileChange = event => {
      const file = event.target.files[0];
      if (!file) return;
  
      this.setState({ uploading: true, error: null });
      const formData = new FormData();
      formData.append('templateFile', file);
  
      fetch('/api/templates/upload', {
        method: 'POST',
        body: formData
      })
      .then(response => response.json())
      .then(data => {
        this.setState({ uploading: false });
        if (data.error) {
          this.setState({ error: data.message });
        } else {
          this.props.onFileSelect(data); // Предполагая, что вы хотите что-то сделать с ответом
        }
      })
      .catch(error => {
        this.setState({ uploading: false, error: error.toString() });
      });
    }
  
    render() {
      const { uploading, error } = this.state;
      return (
        <div>
          <label htmlFor="file-upload" className="btn btn-primary">
            {uploading ? 'Загружается...' : 'Загрузить файл'}
          </label>
          <input
            id="file-upload"
            type="file"
            style={{ display: 'none' }}
            onChange={this.handleFileChange}
            onClick={(event) => event.target.value = null} // Reset input value
          />
          {error && <p className="error">{error}</p>}
        </div>
      );
    }
  }
  

  export default FileUploadComponent;
