export function remove(equipment) {
    return dispatch => {
        const id = equipment.id // Получаем ID оборудования, которое нужно удалить
        axios.delete(`${consts.API_URL}/equipments/${id}`)
            .then(resp => {
                toastr.success('Success', 'Equipment deleted successfully.')
                dispatch({
                    type: 'EQUIPMENTS_DELETED',
                    payload: id // Удаляем оборудование из списка в Redux
                })
            })
            .catch(e => {
                toastr.error('Error', 'Failed to delete equipment.')
                e.response.data.errors.forEach(error => toastr.error('Error', error))
            })
    }
}
